import React, { useState } from "react";
import ResultCard from "./ResultCard";
import LogoImg from "../assets/logo.png";
import GithubImg from "../assets/github.png";

const API_URL = "http://www.omdbapi.com?apikey=6a9fecfd";

const SearchSection = () => {
  const [searchTerm, setSearchTerm] = useState([]);
  const [queries, setQuery] = useState();

  const searchData = async (title) => {
    const reponse = await fetch(`${API_URL}&s=${title}`);
    const data = await reponse.json();
    setQuery(data.Search);
  };

  return (
    <div className="container search-section">
      <div className="search-main">
        <div className="title">
          <h1>This is the title section</h1>
          <div className="titlecard-logo">
            <img src={LogoImg} alt="Logo Better Day" />
          </div>
          <div className="titlecard-icon">
            <span>
              <a href="https://github.com/Nhathuy1305/BetterDay-Tool">
                <img src={GithubImg} alt="Github" />
              </a>
            </span>
          </div>
          <div className="titlecard-intro">
            Lorem ipsum dolor sit amet consectetur adipisicing elit. Sapiente
            expedita maxime consequatur dolorem itaque cum voluptatum cupiditate
            quam cumque nisi, in similique molestias saepe vitae excepturi error
            illum non? Sapiente.
          </div>
          <div className="titlecard-credit">
            <span>Made by BetterDay Team</span>
            <span>Demo Online</span>
          </div>
        </div>
        <div className="input">
          <h1>This is the input section</h1>
          <input
            placeholder="enter query"
            value={searchTerm}
            onChange={(e) => setSearchTerm(e.target.value)}
          ></input>
          <button
            className="generate-query"
            onClick={() => {
              searchData(searchTerm);
            }}
          >
            Generate
          </button>
        </div>
      </div>
      <div className="result">
        <h1>This is the result section</h1>
        <ResultCard queries={queries} />
      </div>
    </div>
  );
};

export default SearchSection;
