import React, { useState } from "react";
import RightSection from "./RightSection";

const API_URL = "http://www.omdbapi.com?apikey=6a9fecfd";

const SearchCard = () => {
  const [searchTerm, setSearchTerm] = useState([]);
  const [query, setQuery] = useState();

  const searchData = async (title) => {
    const reponse = await fetch(`${API_URL}&s=${title}`);
    const data = await reponse.json();

    setImg(data.Search);
  };

  return (
    <div>
      <h1>This is the search section</h1>
      <div className="search-container">
        <div className="input-container">
          <input
            placeholder="enter query"
            value={searchTerm}
            onChange={(e) => setSearchTerm(e.target.value)}
          ></input>
        </div>
        <button className="generate-query" onClick={searchData(searchTerm)}>
          Generate
        </button>
      </div>
      <RightSection query={q} />
    </div>
  );
};

export default SearchCard;
